  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Controllers Overview'
};
export const metadata = {
  "unversionedId": "controllers/controllers-overview",
  "id": "controllers/controllers-overview",
  "isDocsHomePage": false,
  "title": "Controllers Overview",
  "description": "This page contains a list of all available controllers within StreamChat, including their short description and typical use-cases.",
  "source": "@site/docs/controllers/controllers-overview.md",
  "slug": "/controllers/controllers-overview",
  "permalink": "/stream-chat-swift/controllers/controllers-overview",
  "editUrl": "https://github.com/GetStream/stream-chat-swift/edit/main/stream-chat-swift-docs/docs/controllers/controllers-overview.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "attachments-migration",
    "permalink": "/stream-chat-swift/guides/attachments-migration"
  },
  "next": {
    "title": "UI Components Overview",
    "permalink": "/stream-chat-swift/ui-components/ui-components-overview"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`This page contains a list of all available controllers within `}<inlineCode parentName="p">{`StreamChat`}</inlineCode>{`, including their short description and typical use-cases.`}</p>
    <hr></hr>
    <h4 {...{
      "id": "currentchatusercontroller"
    }}><inlineCode parentName="h4">{`CurrentChatUserController`}</inlineCode></h4>
    <p>{`Used for operations related to the current user, like setting the current user, observing its changes, etc.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Current user profile screen, a log-in screen, ...`}</p>
    <h4 {...{
      "id": "chatconnectioncontroller"
    }}><inlineCode parentName="h4">{`ChatConnectionController`}</inlineCode></h4>
    <p>{`Used for observing a connection status of the system and handling the connection manually if `}<inlineCode parentName="p">{`config.shouldConnectAutomatically`}</inlineCode>{` is set to false.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` connection status banner (connecting/connected), ...`}</p>
    <h4 {...{
      "id": "chatusercontroller"
    }}><inlineCode parentName="h4">{`ChatUserController`}</inlineCode></h4>
    <p>{`Used for operations related to any user of a chat, like muting, getting user info, and observing its changes.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` User profile screen, quick actions on a user ("Mute user"), ...`}</p>
    <h4 {...{
      "id": "chatuserlistcontroller"
    }}><inlineCode parentName="h4">{`ChatUserListController`}</inlineCode></h4>
    <p>{`Used to get a list of all users in the chat app. You can also specify `}<inlineCode parentName="p">{`UserListQuery`}</inlineCode>{` to filter the users. Supports pagination.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Show list of all users in the app.`}</p>
    <h4 {...{
      "id": "chatchannellistcontroller"
    }}><inlineCode parentName="h4">{`ChatChannelListController`}</inlineCode></h4>
    <p>{`Used to get a list of all channels in the app matching the provided `}<inlineCode parentName="p">{`ChannelListQuery`}</inlineCode>{`. Supports pagination.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Show a list of channels (the main page of most chat apps)`}</p>
    <h4 {...{
      "id": "chatchannelcontroller"
    }}><inlineCode parentName="h4">{`ChatChannelController`}</inlineCode></h4>
    <p>{`Used for operations related to a channel, like getting all messages, sending new messages, but also adding/removing members or editing the channel details and deleting it.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Messages screen, channel quick actions ("Delete channel", etc.`}</p>
    <h4 {...{
      "id": "chatchannelmembercontroller"
    }}><inlineCode parentName="h4">{`ChatChannelMemberController`}</inlineCode></h4>
    <p>{`Used for operations related to a member of a channel, like banning, getting member info, and observing its changes.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Member profile screen, quick actions on a member ("Ban user"), ...`}</p>
    <h4 {...{
      "id": "chatchannelmemberlistcontroller"
    }}><inlineCode parentName="h4">{`ChatChannelMemberListController`}</inlineCode></h4>
    <p>{`Used to get a list of all members of a channel. You can also specify `}<inlineCode parentName="p">{`MemberListQuery`}</inlineCode>{` to filter the members. Supports pagination.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Show list of all members in the channel.`}</p>
    <h4 {...{
      "id": "chatmessagecontroller"
    }}><inlineCode parentName="h4">{`ChatMessageController`}</inlineCode></h4>
    <p>{`Used for operations related to a message, like deleting the message, adding reacting, and observing its changes.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` Message detail screen, quick actions on a message ("Add Reaction"), ...`}</p>
    <h4 {...{
      "id": "chatusersearchcontroller"
    }}><inlineCode parentName="h4">{`ChatUserSearchController`}</inlineCode></h4>
    <p>{`Used for performing a full-text search in available users by their name and id.`}</p>
    <p><em parentName="p">{`Typical usage:`}</em>{` User mention suggestion, add to channel suggestion, search users screen, ...`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
