  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Migrating from 1.x and 2.x'
};
export const metadata = {
  "unversionedId": "migrating-from-1.x-and-2.x",
  "id": "migrating-from-1.x-and-2.x",
  "isDocsHomePage": false,
  "title": "Migrating from 1.x and 2.x",
  "description": "StreamChat SDKs were re-written for the ground up for v3. Unfortunately that means there isn't a clear, straightforward way to migrate. We suggest reading up on documentation and sample projects to get familiar with the SDK so that you can re-create the functionality with the new SDKs.",
  "source": "@site/docs/migrating-from-1.x-and-2.x.md",
  "slug": "/migrating-from-1.x-and-2.x",
  "permalink": "/stream-chat-swift/migrating-from-1.x-and-2.x",
  "editUrl": "https://github.com/GetStream/stream-chat-swift/edit/main/stream-chat-swift-docs/docs/migrating-from-1.x-and-2.x.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "UI Components Overview",
    "permalink": "/stream-chat-swift/ui-components/ui-components-overview"
  },
  "next": {
    "title": "Frequenty Asked Questions",
    "permalink": "/stream-chat-swift/faq"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Missing Features',
  id: 'missing-features',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`StreamChat SDKs were re-written for the ground up for v3. Unfortunately that means there isn't a clear, straightforward way to migrate. We suggest reading up on documentation and sample projects to get familiar with the SDK so that you can re-create the functionality with the new SDKs.`}</p>
    <p><strong parentName="p">{`Please don't upgrade to version 3.0 before you get familiar with the changes and their impact on your codebase.`}</strong></p>
    <p>{`To prevent CocoaPods from updating `}<inlineCode parentName="p">{`StreamChat`}</inlineCode>{` to version 3, you can explicitly pin the SDKs to versions 2.x in your `}<inlineCode parentName="p">{`podfile`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ruby"
      }}>{`pod 'StreamChat', '~> 2.0'
pod 'StreamChatCore', '~> 2.0' # if needed
pod 'StreamChatClient', '~> 2.0' # if needed
`}</code></pre>
    <h2 {...{
      "id": "missing-features"
    }}>{`Missing Features`}</h2>
    <p>{`Some features are missing from the new versions so if you're using these, please hold on to upgrade until they're implemented. We're constantly improving and adding new features and we don't expect this "transitioning phase" to last long. v3 will catch up to v2 in terms of features very soon.`}</p>
    <p>{`If you're actively using one of these features, please hold off upgrading until we add these in.`}</p>
    <ul>
      <li parentName="ul">{`Local notifications`}</li>
      <li parentName="ul">{`RxSwift wrappers`}</li>
      <li parentName="ul">{`Multi-tenancy (teams)`}</li>
      <li parentName="ul">{`Silent messages`}</li>
      <li parentName="ul">{`Expired tokens`}</li>
      <li parentName="ul">{`Channel invites`}</li>
      <li parentName="ul">{`Paginating reactions`}</li>
      <li parentName="ul">{`Message search`}</li>
    </ul>
    <h1 {...{
      "id": "streamchat-v1x--v2x---streamchatui-v3"
    }}>{`StreamChat v1.x / v2.x -> StreamChatUI v3`}</h1>
    <p>{`None of the UI SDK components we had in v1.x / v2.x were carried over to StreamChatUI and it was written from the ground up. The new UI SDK is much more component-focused and each component can be used separately. Please check `}<a parentName="p" {...{
        "href": "https://github.com/GetStream/stream-chat-swift/wiki/UI-Cheat-Sheet"
      }}>{`UI Cheat Sheet`}</a>{` to get familiar on how to use it and convert your screens.`}</p>
    <h1 {...{
      "id": "streamchatcore-v1x--v2x-and-streamchatclient-v2x---streamchat-v3"
    }}>{`StreamChatCore v1.x / v2.x and StreamChatClient v2.x -> StreamChat v3`}</h1>
    <p>{`Our low-level frameworks (`}<inlineCode parentName="p">{`Client`}</inlineCode>{` + `}<inlineCode parentName="p">{`Core`}</inlineCode>{`) were merged and renamed to `}<inlineCode parentName="p">{`StreamChat`}</inlineCode>{`. If you use only our low-level frameworks, you can use our `}<a parentName="p" {...{
        "href": "https://github.com/GetStream/stream-chat-swift/wiki/Cheat-Sheet"
      }}>{`cheat sheet`}</a>{` to get more familiar with the update API and better assess the impact of the changes on your codebase.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
