export const toc = [{
  value: 'Send attachments',
  id: 'send-attachments',
  children: [{
    value: 'File/image attachments',
    id: 'fileimage-attachments',
    children: []
  }, {
    value: 'Custom attachments',
    id: 'custom-attachments',
    children: []
  }, {
    value: 'Summary',
    id: 'summary',
    children: []
  }]
}, {
  value: 'Get attachments',
  id: 'get-attachments',
  children: [{
    value: 'File attachments',
    id: 'file-attachments',
    children: []
  }, {
    value: 'Image attachments',
    id: 'image-attachments',
    children: []
  }, {
    value: 'Giphy attachments',
    id: 'giphy-attachments',
    children: []
  }, {
    value: 'Link preview attachments',
    id: 'link-preview-attachments',
    children: []
  }, {
    value: 'Custom attachments',
    id: 'custom-attachments-1',
    children: []
  }, {
    value: 'Changes summary',
    id: 'changes-summary',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "attachments-v4x-migration"
    }}>{`Attachments v4.x migration`}</h1>
    <h2 {...{
      "id": "send-attachments"
    }}>{`Send attachments`}</h2>
    <p>{`In both `}<inlineCode parentName="p">{`v4.x`}</inlineCode>{` and `}<inlineCode parentName="p">{`v3.2`}</inlineCode>{` the sequence of steps is the same:`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`[1]`}</strong>{` create a controller for the channel the message should be sent to`}</li>
      <li parentName="ul"><strong parentName="li">{`[2]`}</strong>{` create attachments that should be added to the message`}</li>
      <li parentName="ul"><strong parentName="li">{`[3]`}</strong>{` send the message with attachments using the controller`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-swift",
        "metastring": "{7-9}",
        "{7-9}": true
      }}>{`// [1] Create a controller
let channelController = ChatChannelController(
    for: ChannelId(type: .messaging, id: "general")
)

// [2] Create attachments (ðŸš¨ has changed in \`v4.x\`)
let attachments = [
  ...
]

// [3] Send the message
channelController.createNewMessage(
    text: "Hey, have a look at this one",
    attachments: attachments,
    completion: { result in
      // handle the result
      print(result)
    }
)
`}</code></pre>
    <p>{`Let's see how to create `}<strong parentName="p">{`file/image`}</strong>{` and `}<strong parentName="p">{`custom`}</strong>{` attachments in `}<inlineCode parentName="p">{`v4.x`}</inlineCode>{` and make the compiler happy.`}</p>
    <h3 {...{
      "id": "fileimage-attachments"
    }}>{`File/image attachments`}</h3>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Create an array of \`AttachmentEnvelope\` objects
let attachments: [AttachmentEnvelope] = [
  // Add file attachment by creating \`ChatMessageAttachmentSeed\`
  ChatMessageAttachmentSeed(localURL: fileURL, type: .file),
  // Add image attachment by creating \`ChatMessageAttachmentSeed\`
  ChatMessageAttachmentSeed(localURL: imageURL, type: .image)
]
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Create an array of \`AnyAttachmentPayload\` objects
let attachments: [AnyAttachmentPayload] = [
  // Add file attachment by creating \`AnyAttachmentPayload\`
  try AnyAttachmentPayload(localFileURL: fileURL, attachmentType: .file),
  // Add image attachment by creating \`AnyAttachmentPayload\`
  try AnyAttachmentPayload(localFileURL: imageURL, attachmentType: .image)
]
`}</code></pre>
    <p>{`The `}<inlineCode parentName="p">{`.file`}</inlineCode>{` and `}<inlineCode parentName="p">{`.image`}</inlineCode>{` attachments are the only built-in attachment types that can be added to the message manually.`}</p>
    <h3 {...{
      "id": "custom-attachments"
    }}>{`Custom attachments`}</h3>
    <p>{`To add a custom attachments to the message the custom type has to be created first.
This is true for both `}<inlineCode parentName="p">{`v3.2`}</inlineCode>{` and `}<inlineCode parentName="p">{`v4.x`}</inlineCode>{` however there're some differences so let's see what they are:`}</p>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Declare a custom type conforming to \`AttachmentEnvelope\`
struct Product: AttachmentEnvelope {
    let type: AttachmentType = .custom("product")

    let name: String
    let price: Int
}

// Create a custom attachment
let iPhone = Product(name: "iPhone 12 Pro", price: 999)

// Create an array of \`AttachmentEnvelope\` objects
let attachments: [AttachmentEnvelope] = [
  // Add custom attachment instance directly
  iPhone
]
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Declare a custom type conforming to \`AttachmentPayload\`
struct Product: AttachmentPayload {
    static let type: AttachmentType = "product"

    let name: String
    let price: Int
}

// Create an attachment payload
let iPhone = Product(name: "iPhone 12 Pro", price: 999)

// Create an array of \`AnyAttachmentPayload\` objects
let attachments: [AnyAttachmentPayload] = [
  // Create \`AnyAttachmentPayload\` that wraps custom attachment payload
  AnyAttachmentPayload(payload: iPhone)
]
`}</code></pre>
    <h3 {...{
      "id": "summary"
    }}>{`Summary`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="th">{`v3.2`}</inlineCode></th>
          <th parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="th">{`v4.x`}</inlineCode></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`An array of `}<inlineCode parentName="td">{`AttachmentEnvelope`}</inlineCode>{` objects is passed when creating a message`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`An array of `}<inlineCode parentName="td">{`AnyAttachmentPayload`}</inlineCode>{` is passed when creating a message`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Attachments of `}<inlineCode parentName="td">{`.file/.image`}</inlineCode>{` type are added via `}<inlineCode parentName="td">{`ChatMessageAttachmentSeed`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Attachments of `}<inlineCode parentName="td">{`.file/.image`}</inlineCode>{` type are added via `}<inlineCode parentName="td">{`AnyAttachmentPayload`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Custom attachment must conform to `}<inlineCode parentName="td">{`AttachmentEnvelope`}</inlineCode>{` protocol and can be directly passed to `}<inlineCode parentName="td">{`createMessage`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Custom attachment must conform to `}<inlineCode parentName="td">{`AttachmentPayload`}</inlineCode>{` protocol and wrapped by `}<inlineCode parentName="td">{`AnyAttachmentPayload`}</inlineCode>{` before passing to `}<inlineCode parentName="td">{`createMessage`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "get-attachments"
    }}>{`Get attachments`}</h2>
    <p>{`In both `}<inlineCode parentName="p">{`v4.x`}</inlineCode>{` and `}<inlineCode parentName="p">{`v3.2`}</inlineCode>{` the sequence of steps is the same:`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`[1]`}</strong>{` get a `}<inlineCode parentName="li">{`ChatMessage`}</inlineCode>{` model (`}<a parentName="li" {...{
          "href": "working-with-messages"
        }}>{`Working with messages`}</a>{`)`}</li>
      <li parentName="ul"><strong parentName="li">{`[2]`}</strong>{` get all attachments of the required type`}</li>
      <li parentName="ul"><strong parentName="li">{`[3]`}</strong>{` access attachment fields`}</li>
    </ul>
    <p>{`Let's see how at steps `}<strong parentName="p">{`[2]`}</strong>{` and `}<strong parentName="p">{`[3]`}</strong>{` look in `}<inlineCode parentName="p">{`v4.x`}</inlineCode>{` for different kind of attachments.`}</p>
    <h3 {...{
      "id": "file-attachments"
    }}>{`File attachments`}</h3>
    <p>{`File attachment requires prior uploading before the message is sent. The local state is exposed for `}<inlineCode parentName="p">{`.file`}</inlineCode>{` attachments to track how the uploading goes.`}</p>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get \`.file\` attachments
let fileAttachments = message.attachments
  .compactMap { $0 as? ChatMessageDefaultAttachment }
  .filter { $0.type == .file }

// Get the first attachment
if let file = fileAttachments.first {
  if let localState = file.localState, let localURL = file.localURL {
    // Attachment is being uploaded, use local URL to show a preview
    print(localState, localURL)
  } else if let fileURL = file.url {
    // Attachment is uploaded, use remote url
    print(fileURL)
  }
}
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get the first \`.file\` attachment
if let file = message.fileAttachments.first {
  // Show file preview url. If attachment is being uploaded this will be the local URL.
  print(file.assetURL)

  if let uploadingState = file.uploadingState {
    // Attachment is being uploaded, handle uploading progress
    print(uploadingState)
  }
}
`}</code></pre>
    <h3 {...{
      "id": "image-attachments"
    }}>{`Image attachments`}</h3>
    <p>{`Image attachment requires prior uploading before the message is sent. The local state is exposed for `}<inlineCode parentName="p">{`.image`}</inlineCode>{` attachments to track how the uploading goes.`}</p>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <p>{`Image attachments are exposed as `}<inlineCode parentName="p">{`ChatMessageDefaultAttachment`}</inlineCode>{`. Mandatory fields are `}<strong parentName="p">{`optinal`}</strong>{` because of `}<inlineCode parentName="p">{`ChatMessageDefaultAttachment`}</inlineCode>{` being used for all built-in attachment types.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get \`.image\` attachments
let imageAttachments = message.attachments
  .compactMap { $0 as? ChatMessageDefaultAttachment }
  .filter { $0.type == .image }

// Get the first attachment
if let image = imageAttachments.first {
  if let localState = image.localState, let localURL = image.localURL {
    // Attachment is being uploaded, use local URL to show a preview
    print(localState, localURL)
  } else if let imageURL = image.imageURL {
    // Attachment is uploaded, use remote url
    print(imageURL)
  }
}
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <p>{`Image attachments are exposed as `}<inlineCode parentName="p">{`ChatMessageImageAttachment`}</inlineCode>{`. Mandatory fields are `}<strong parentName="p">{`non-optinal`}</strong>{` and can be accessed directly on attachment.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get the first \`.image\` attachment
if let image = message.imageAttachments.first {
  // Show a preview
  print(image.previewURL)

  if let uploadingState = image.uploadingState {
    // Attachment is being uploaded, handle uploading progress
    print(uploadingState)
  }
}
`}</code></pre>
    <h3 {...{
      "id": "giphy-attachments"
    }}>{`Giphy attachments`}</h3>
    <p>{`The ephemeral message containing giphy attachment will be created when `}<inlineCode parentName="p">{`/giphy`}</inlineCode>{` command is used.`}</p>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <p>{`Giphy attachments are exposed as `}<inlineCode parentName="p">{`ChatMessageDefaultAttachment`}</inlineCode>{`. Mandatory fields are `}<strong parentName="p">{`optinal`}</strong>{` because of `}<inlineCode parentName="p">{`ChatMessageDefaultAttachment`}</inlineCode>{` being used for all built-in attachment types.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get \`.giphy\` attachments
let giphyAttachments = message.attachments
  .compactMap { $0 as? ChatMessageDefaultAttachment }
  .filter { $0.type == .giphy }

// Get the first attachments
if let giphy = giphyAttachments.first {
  // Unwrap gif URL
  if let gifURL = giphy.imageURL {
    // Load and show gif
    print(gifURL)
  }
}
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <p>{`Giphy attachments are exposed as `}<inlineCode parentName="p">{`ChatMessageGiphyAttachment`}</inlineCode>{`. Mandatory fields are `}<strong parentName="p">{`non-optinal`}</strong>{` and can be accessed directly on attachment.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get the first \`.giphy\` attachment
if let giphy = message.giphyAttachments.first {
  // Load and show gif right away
  print(giphy.previewURL)
}
`}</code></pre>
    <h3 {...{
      "id": "link-preview-attachments"
    }}>{`Link preview attachments`}</h3>
    <p>{`The link attachment will be added to the message automatically if the message is sent with the text containing the URL.`}</p>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <p>{`Giphy attachments are exposed as `}<inlineCode parentName="p">{`ChatMessageDefaultAttachment`}</inlineCode>{`. Mandatory fields are `}<strong parentName="p">{`optinal`}</strong>{` because of `}<inlineCode parentName="p">{`ChatMessageDefaultAttachment`}</inlineCode>{` being used for all built-in attachment types.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get \`.link(...)\` attachments
let linkAttachments = message.attachments
  .compactMap { $0 as? ChatMessageDefaultAttachment }
  .filter { $0.type.isLink }

// Get the first attachment
if let link = linkAttachments.first {
  // Unwrap the URL
  if let url = link.url {
    // Show preview for url
    print(url)
  }
}
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <p>{`Link preview attachments are exposed as `}<inlineCode parentName="p">{`ChatMessageLinkAttachment`}</inlineCode>{`. Mandatory fields are `}<strong parentName="p">{`non-optinal`}</strong>{` and can be accessed directly on attachment.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Get the first \`.linkPreview\` attachment
if let linkPreview = message.linkAttachments.first {
  // Handle the link
  print(linkPreview.originalURL)
}
`}</code></pre>
    <h3 {...{
      "id": "custom-attachments-1"
    }}>{`Custom attachments`}</h3>
    <p><inlineCode parentName="p">{`Version 3.x`}</inlineCode></p>
    <ul>
      <li parentName="ul">{`all built-in attachments exposed as `}<inlineCode parentName="li">{`ChatMessageRawAttachment`}</inlineCode></li>
      <li parentName="ul">{`custom payload is stored in `}<inlineCode parentName="li">{`data: Data?`}</inlineCode>{` fields`}</li>
      <li parentName="ul">{`custom data should be decoded manually `}<inlineCode parentName="li">{`data`}</inlineCode></li>
      <li parentName="ul">{`attachment `}<inlineCode parentName="li">{`id`}</inlineCode>{` is optional`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`// Custom attachment type has to be \`Decodable\`
extension Product: Decodable { /* ... */ }

// Get attachments of custom type
let productAttachments = message.attachments
  .compactMap { $0 as? ChatMessageRawAttachment }
  .filter { $0.type == .custom("product") }

// Get first custom attachment
if let productAttachment = productAttachments.first {
  // Unwrap attachment data
  if let productData = productAttachment.data {
    // Try to decode the custom type from data
    let product = try JSONDecoder().decode(Product.self, from: productData)
    // Handle custom attachment payload
    print(product)
  }
}
`}</code></pre>
    <p><inlineCode parentName="p">{`Version 4.x`}</inlineCode></p>
    <ul>
      <li parentName="ul">{`custom attachments are directly accessible on `}<inlineCode parentName="li">{`ChatMessage`}</inlineCode></li>
      <li parentName="ul">{`custom payload fields are are directly on attachment thanks to `}<inlineCode parentName="li">{`dynamicMemberLookup`}</inlineCode></li>
      <li parentName="ul">{`attachment `}<inlineCode parentName="li">{`id`}</inlineCode>{` is not-optional ðŸŽ‰`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`
// It's recommended but not required to create a typealias for custom attachment type to avoid generic stuff
typealias ProductAttachment = _ChatMessageAttachment<Product>

// Get attachments of custom type
let productAttachments = message.attachments(payloadType: Product.self)

// Get first custom attachment
if let product = productAttachments.first {
  // Access the payload fields right away
  print(product.name)
}
`}</code></pre>
    <h3 {...{
      "id": "changes-summary"
    }}>{`Changes summary`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="th">{`v3.2`}</inlineCode></th>
          <th parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="th">{`v4.x`}</inlineCode></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`ChatMessage`}</inlineCode>{` has a single `}<inlineCode parentName="td">{`attachments`}</inlineCode>{` field`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`ChatMessage`}</inlineCode>{` has multiple fields one for each attachment type (`}<inlineCode parentName="td">{`imageAttachments/giphyAttachments/etc.`}</inlineCode>{`)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Built-in attachments are exposed as `}<inlineCode parentName="td">{`ChatMessageDefaultAttachment`}</inlineCode>{` with mandatory fields being `}<strong parentName="td">{`optional`}</strong></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Built-in attachments are exposed as arrays `}<inlineCode parentName="td">{`_ChatMessageAttachment<Payload>`}</inlineCode>{` with concrete payload type with mandatory fields being `}<strong parentName="td">{`non-optional`}</strong></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`To be exposed on the message custom attachment must conform to `}<inlineCode parentName="td">{`ChatMessageAttachment`}</inlineCode>{` protocol`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`In order attachment can be exposed on the message it's payload must conform to `}<inlineCode parentName="td">{`AttachmentPayload`}</inlineCode>{` protocol`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Custom attachments are exposed as `}<inlineCode parentName="td">{`ChatMessageRawAttachment`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Custom attachments are exposed the same way built-in attachments are`}</td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
