  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Glossary'
};
export const metadata = {
  "unversionedId": "glossary",
  "id": "glossary",
  "isDocsHomePage": false,
  "title": "Glossary",
  "description": "A list of names and terms used in the framework and documentation.",
  "source": "@site/docs/glossary.md",
  "slug": "/glossary",
  "permalink": "/stream-chat-swift/glossary",
  "editUrl": "https://github.com/GetStream/stream-chat-swift/edit/main/stream-chat-swift-docs/docs/glossary.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Frequenty Asked Questions",
    "permalink": "/stream-chat-swift/faq"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`A list of names and terms used in the framework and documentation.`}</p>
    <hr></hr>
    <ul>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`Client`}</strong>{` or `}<strong parentName="p">{`ChatClient`}</strong>{`: The root object of the SDK representing the chat service. Typically, an app contains just one instance of `}<inlineCode parentName="p">{`ChatClient`}</inlineCode>{`. However, it's possible to have multiple instances if your use case requires it (i.e. more than one window with different workspaces in a Slack-like app).`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`User:`}</strong>{` A user entity in a chat app. It's uniquely identified by a `}<inlineCode parentName="p">{`UserId`}</inlineCode>{`. Users can't be created directly from the Swift SDK. They must be created by your backend service.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`CurrentUser:`}</strong>{` A user entity that is currently signed-in. Users can't be created directly from the Swift SDK. Your backend service which should provide the Swift SDK with a token to authenticate the current user with.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`Channel:`}</strong>{` A channel contains messages, a list of people that are watching the channel, and optionally a list of members (for private channels). In other words - when you open a messaging the app, the various conversations you see there are "channels". A channel can be a private conversation between two users or a public live-stream-like channel with thousands of users watching it.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`Member:`}</strong>{` If a user belongs to a specific channel it becomes its member. A member is an entity representing a user that belongs to a given channel. It contains some additional information about the channel membership. One "user" entity can be represented as multiple "member" entities in various channels.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`Watcher:`}</strong>{` If a channel is public, it's visible to all users. A user that actively observes a channel is called a "watcher". `}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p">{`Controller:`}</strong>{` Controller objects are the primary way of interacting with the chat service. Controller objects are lightweight and can be easily created and destroyed as needed. Controllers can be used for both, continuous data observation, and for quick data mutations. It's possible (and very common) that you have multiple controllers representing a single entity.`}</p>
      </li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
