  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'StreamChatUI Cheat Sheet'
};
export const metadata = {
  "unversionedId": "ui-cheat-sheet",
  "id": "ui-cheat-sheet",
  "isDocsHomePage": false,
  "title": "StreamChatUI Cheat Sheet",
  "description": "This cheat sheet provides additional information on how to use StreamChatUI SDK. It offers rich code snippets, detailed explanations, and commentary on the provided solutions.",
  "source": "@site/docs/ui-cheat-sheet.md",
  "slug": "/ui-cheat-sheet",
  "permalink": "/stream-chat-swift/ui-cheat-sheet",
  "editUrl": "https://github.com/GetStream/stream-chat-swift/edit/main/stream-chat-swift-docs/docs/ui-cheat-sheet.md",
  "version": "current"
};
