export const toc = [{
  value: 'Setup',
  id: 'setup',
  children: [{
    value: 'Integrating with <code>StreamChat</code>',
    id: 'integrating-with-streamchat',
    children: []
  }]
}, {
  value: 'Customizing views',
  id: 'customizing-views',
  children: [{
    value: 'View Lifecycle Methods',
    id: 'view-lifecycle-methods',
    children: []
  }, {
    value: 'Changing Main Color',
